<?php $con=mysqli_connect("localhost","root","root","bluechat"); if (mysqli_connect_errno()) { echo "Failed to connect to MySQL: " . mysqli_connect_error(); } $result = mysqli_query($con,"SELECT  GROUP_CONCAT(msgtext ORDER BY msgid ASC SEPARATOR '~') FROM usermessages WHERE msguserid = 'rford'; "); while($row = mysqli_fetch_array($result)){ echo $row[0]; } mysqli_close($con); ?>
